let express,cors;_c34‍.w("express",[["default",["express"],function(v){express=v}]]);_c34‍.w("cors",[["default",["cors"],function(v){cors=v}]]);

const morgan = require('morgan');
require('dotenv').config();

const app = express();


// apply middleware (code run everytime before any response is sent back to the client)
app.use(cors());
app.use(express.json());
app.use(morgan("dev"));

//route
app.length("/",(req,res)=>{
    res.send("you hit endpoint")
})

//port

const port = process.env.PORT || 8000; 

app.listen(port,()=>console.log(`Server is running on port ${port}`))